import React from 'react';
import { usePipelineState, useAdversarialAttempts, useErrorClicks, useAdversarialPipeline } from './hooks.js';
import { generateDifficultyCategories } from './utils.js';
import Header from './components/Header.js';
import AntiCheatAlert from './components/AntiCheatAlert.js';
import TopicInput from './components/TopicInput.js';
import DifficultySelection from './components/DifficultySelection.js';
import GenerationLog from './components/GenerationLog.js';
import AdversarialAttempts from './components/AdversarialAttempts.js';
import AssessmentQuestion from './components/AssessmentQuestion.js';
import ResultsDisplay from './components/ResultsDisplay.js';
import Footer from './components/Footer.js';

const AqumenAdvancedDemoModular = () => {
  // Custom hooks for state management
  const pipelineState = usePipelineState();
  const adversarialHooks = useAdversarialAttempts();
  const errorClickHooks = useErrorClicks();
  const { runAdversarialPipeline } = useAdversarialPipeline(pipelineState, adversarialHooks);

  // Destructure states for easier access
  const { 
    currentStep, 
    userTopic, 
    difficultyCategories, 
    selectedSubtopic, 
    finalQuestion, 
    showResults, 
    isGenerating, 
    antiCheatTriggered,
    setCurrentStep,
    setUserTopic,
    setDifficultyCategories,
    setSelectedDifficulty,
    setSelectedSubtopic,
    setShowResults,
    setIsGenerating
  } = pipelineState;

  const { 
    adversarialAttempts, 
